import React, { useState } from "react"
import ExpensesFilter from "../ExpenseFilter/ExpensesFilter"
import Card from "../UI/Card"
import ExpenseItems from "./ExpenseItems"
import './Expenses.css'

const Expenses = (props) => {

    const [filteredYear, setFilteredYear] = useState('2020')

    const selectedYearFilter = (selectedYear) => {
        setFilteredYear(selectedYear);

        // const checkYear = (selectedYear) => {
        //     return props.expenses.date === selectedYear
        // }

        // const filterList = props.expenses.filter(checkYear)

    }



    return (
        <Card className="expenses">
            <ExpensesFilter initialSelect={filteredYear} onSelectedYearFilter={selectedYearFilter}/>
            {console.log(setFilteredYear)}
            {
                props.expenses.map((items) => (
                    <ExpenseItems 
                        key={items.id}
                        title={items.title} 
                        amount={items.amount} 
                        date={items.date} 
                    /> 
                    )
                )
            }
        </Card>
    )
}

export default Expenses
