import React, { useState } from 'react';

import './ExpensesFilter.css';

const ExpensesFilter = (props) => {

  const [selectedYear, setSelectedYear] = useState('');

  const onChangeHandler = (event) => {
    setSelectedYear(event.target.value);
    props.onSelectedYearFilter(event.target.value)
  };

  return (
    <div className='expenses-filter'>
      <div className='expenses-filter__control'>
        <label>Filter by year</label>
        <select onChange={onChangeHandler}>
          <option value='2022'>{selectedYear}2022</option>
          <option value='2021'>{selectedYear}2021</option>
          <option value='2020'>{selectedYear}2020</option>
          <option value='2019'>{selectedYear}2019</option>
        </select>
      </div>
    </div>
  );
};

export default ExpensesFilter;
